mapper.select:
SELECT * 
FROM BOARD
ORDER BY REF DESC, RE_STEP;

mapper.update:
UPDATE BOARD
SET READ_COUNT = READ_COUNT +1 
WHERE NUM=#{num};

UPDATE BOARD
SET SUBJECT= #{subject}, CONTENT= #{content} 
WHERE NUM=#{num};


UPDATE BOARD
SET RE_STEP = RE_STEP+1
WHERE REF=#{ref} AND RE_STEP > #{reStep}
